pragma solidity =0.8.18;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";

import { IFlashLoanTaker } from "./FlashloanTaker.sol";
import { Route } from "./libraries/Route.sol";

contract Arbitrage is OwnableUpgradeable {
  using SafeERC20Upgradeable for IERC20Upgradeable;

  string public constant ADDRESS_ZERO = "Address zero";
  string public constant NOTHING_TO_CLAIM = "Nothing to claim";
  string public constant DUPLICATE = "Duplicate";

  event Init(address indexed flashloan);
  event FlashloanSet(address indexed flashloan);
  event ClaimedProfit(
    address indexed token,
    address indexed receiver,
    uint256 amount
  );
  event ArbitrageMade(
    uint256 indexed flashloanAmount,
    uint256 indexed amountOutMin,
    Route.SinglePath[] route
  );

  /*
    Arbitrage path may go through multiple based dexes

    path: 
    0) [token0_0, token0_1], dex0 (FlashSwap base token == token0_0, loan token == token0_1)
    1) [token1_0, token1_1, ... , token1_m], dex1
    2) [token2_0, token2_1, ... , token2_k], dex2
    ...
    N) [tokenN_0, tokenN_1, ... , tokenN_l], dexN

    Flash Swap: tokenN_l in (profit) == token0_0, token0_n out (loan)
  */

  address public flashloan;

  constructor() {
    _disableInitializers();
  }

  function init(address _flashloan) external initializer {
    require(_flashloan != address(0), ADDRESS_ZERO);
    flashloan = _flashloan;
    _transferOwnership(msg.sender);
    emit Init(_flashloan);
  }

  function resetFlashloan(address _flashloan) external onlyOwner {
    require(_flashloan != address(0), ADDRESS_ZERO);
    require(_flashloan != flashloan, DUPLICATE);
    flashloan = _flashloan;
    emit FlashloanSet(_flashloan);
  }

  function makeArbitrage(
    bool exactDebt,
    uint256 flashloanAmount,
    uint256 amountOutMin,
    Route.SinglePath[] calldata route
  ) external onlyOwner returns(uint256 profit) {
    Route.validateRoute(route);

    profit = IFlashLoanTaker(flashloan).executeFlashSwap(
      exactDebt,
      Route.encode(flashloanAmount, amountOutMin, route)
    );

    emit ArbitrageMade(flashloanAmount, amountOutMin, route);
  }

  function claimProfit(address token) external onlyOwner {
    require(token != address(0), ADDRESS_ZERO);

    uint256 balance = IERC20Upgradeable(token).balanceOf(address(this));
    require(balance != 0, NOTHING_TO_CLAIM);

    emit ClaimedProfit(token, msg.sender, balance);
    IERC20Upgradeable(token).safeTransfer(msg.sender, balance);
  }
}
","keccak256":"0x237e2ac6bda4e415b81b35db89db189f7f9614bb6ba694d9f628c3018af609d9","license":"MIT"},"contracts/FlashloanTaker.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity =0.8.18;

import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Callee.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import { ITrader } from "./Trader.sol";

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/Address.sol";

import { Route } from "./libraries/Route.sol";

interface IFlashLoanTaker {
  function executeFlashSwap(
    bool exactDebt,
    bytes calldata routeData
  ) external returns (uint256 profit);
}

contract FlashLoanTaker is
  IFlashLoanTaker,
  IUniswapV2Callee,
  Ownable,
  ReentrancyGuard
{
  using SafeERC20 for IERC20;

  // ------------ ERRORS ------------

  string public constant ADDRESS_ZERO = "Address zero";
  string public constant NO_PROFIT = "No profit";
  string public constant WRONG_CALLER = "Wrong caller";
  string public constant NOT_ALLOWED = "Not allowed";
  string public constant NOT_EXISTS = "Pair not exists";
  string public constant DUPLICATE = "Duplicate";
  string public constant NOT_CONTRACT = "Not contract";
  string public constant NOT_EXECUTOR = "Not executor";

  // ------------- EVENTS --------------

  event SetTrader(address indexed newTrader);
  event SetExecutor(address indexed newExecutor);
  event ExecutedFlashSwap(
    address indexed tokenProfit,
    address indexed tokenLoan,
    uint256 amountFlashLoan,
    uint256 profit
  );

  // trader contract
  address public trader;
  // executor account (arbitrage contract), may be set in post init
  address public executor;

  constructor(address _trader) {
    _setTrader(_trader);
    _transferOwnership(msg.sender);
  }

  function setExecutor(address newExecutor) external onlyOwner {
    require(newExecutor != address(0), ADDRESS_ZERO);
    require(newExecutor != executor, DUPLICATE);

    executor = newExecutor;
    emit SetExecutor(newExecutor);
  }

  function setTrader(address newTrader) external onlyOwner {
    _setTrader(newTrader);
    emit SetTrader(newTrader);
  }

  function uniswapV2Call(
    address sender,
    uint,
    uint,
    bytes calldata data
  ) external override nonReentrant {
    (
      uint256 targetAmount,
      uint256 amountIn,
      bytes memory routeData
    ) = _decodeUniswapV2CallData(data);
    (, , Route.SinglePath[] memory route) = Route.decodeRouteData(routeData);

    IUniswapV2Router02 uniV2Router = IUniswapV2Router02(route[0].router);

    (address tokenProfit, address tokenLoan) = Route.getFlashloanTokens(route);

    address pair = IUniswapV2Factory(uniV2Router.factory()).getPair(
      tokenProfit,
      tokenLoan
    );

    // check that flash swap was called from this contract
    require(sender == address(this), NOT_ALLOWED);
    // check that real UniswapV2Pair calls the callback
    require(msg.sender == pair, WRONG_CALLER);

    /*
        contract has already received amount of tokenFlashLoan
        trader has to make profit and gain target amount of tokenProfit

        amountOut = amount
        target amount = getAmountIn(tokenFlashLoan)
    */

    // send all Flash Loan amount to trader for the future operations
    IERC20(tokenLoan).safeTransfer(trader, amountIn);

    // execute trading operations
    uint256 realAmountOut = ITrader(trader).execute(routeData);

    // expect we have sellTokensAfter - sellTokensBefore >= targetAmount
    // refill UniswapV2Pair contract to finish flash swap execution
    require(realAmountOut > targetAmount, NO_PROFIT);

    // fulfill pair flash swap
    IERC20(tokenProfit).safeTransfer(pair, targetAmount);
  }

  function executeFlashSwap(
    bool exactDebt,
    bytes calldata routeData
  ) external returns (uint256 profit) {
    require(msg.sender == executor, NOT_EXECUTOR);

    (uint256 amountFlashLoan, , Route.SinglePath[] memory route) = Route
      .decodeRouteData(routeData);

    // flashloan router
    IUniswapV2Router02 uniV2Router = IUniswapV2Router02(route[0].router);

    (address tokenProfit, address tokenLoan) = Route.getFlashloanTokens(route);

    // execute swap with a funds gaining callback
    address pair = IUniswapV2Factory(uniV2Router.factory()).getPair(
      tokenProfit,
      tokenLoan
    );

    require(pair != address(0), NOT_EXISTS);

    uint256 targetAmount;
    uint256 amount0Out;
    uint256 amount1Out;
    bytes memory data;

    if (!exactDebt) {
      // exact Loan => exact out amount from flashswap
      (targetAmount, amount0Out, amount1Out, , ) = _calculateAmountsExactLoan(
        amountFlashLoan,
        tokenLoan,
        pair,
        uniV2Router
      );
      // prepare calldata from uniV2 swap to execute callback
      data = abi.encode(targetAmount, amountFlashLoan, routeData);
    } else {
      // exact Debt => exact in amount into flashswap
      (targetAmount, amount0Out, amount1Out, , ) = _calculateAmountsExactDebt(
        amountFlashLoan,
        tokenLoan,
        pair,
        uniV2Router
      );

      // prepare calldata from uniV2 swap to execute callback
      data = abi.encode(targetAmount, amount0Out + amount1Out, routeData);
    }

    uint256 tokensBefore = IERC20(tokenProfit).balanceOf(address(this));

    // performs a flash swap with gaining profit in uniswapV2Call => trader
    // flash swap reverts if profit - fees <= 0
    IUniswapV2Pair(pair).swap(amount0Out, amount1Out, address(this), data);

    // calculate flash trade profit
    profit = IERC20(tokenProfit).balanceOf(address(this)) - tokensBefore;

    // send profit to caller (arbitrage contract)
    IERC20(tokenProfit).safeTransfer(msg.sender, profit);

    emit ExecutedFlashSwap(tokenProfit, tokenLoan, amountFlashLoan, profit);
  }

  function _decodeUniswapV2CallData(
    bytes memory data
  )
    private
    pure
    returns (uint256 targetAmount, uint256 amountIn, bytes memory routeBytes)
  {
    // ABI decode params
    (targetAmount, amountIn, routeBytes) = abi.decode(
      data,
      (uint256, uint256, bytes)
    );
  }

  function _calculateAmountsExactLoan(
    uint256 amountFlashLoan,
    address tokenLoan,
    address pair,
    IUniswapV2Router02 uniV2Router
  )
    private
    view
    returns (
      uint256 targetAmount,
      uint256 amount0Out,
      uint256 amount1Out,
      uint256 reserveIn,
      uint256 reserveOut
    )
  {
    // get reserves to calculate the amountSell required for amount amount
    (uint256 reserve0, uint256 reserve1, ) = IUniswapV2Pair(pair).getReserves();

    if (IUniswapV2Pair(pair).token0() == tokenLoan) {
      amount0Out = amountFlashLoan;
      reserveIn = reserve1;
      reserveOut = reserve0;
    } else {
      amount1Out = amountFlashLoan;
      reserveIn = reserve0;
      reserveOut = reserve1;
    }

    // calculate the targetAmountIn required for swap
    targetAmount = uniV2Router.getAmountIn(
      amountFlashLoan,
      reserveIn,
      reserveOut
    );
  }

  function _calculateAmountsExactDebt(
    uint256 amountFlashLoan,
    address tokenLoan,
    address pair,
    IUniswapV2Router02 uniV2Router
  )
    private
    view
    returns (
      uint256 targetAmount,
      uint256 amount0Out,
      uint256 amount1Out,
      uint256 reserveIn,
      uint256 reserveOut
    )
  {
    // get reserves to calculate the amountSell required for amount amount
    (uint256 reserve0, uint256 reserve1, ) = IUniswapV2Pair(pair).getReserves();

    address token0 = IUniswapV2Pair(pair).token0();
    if (token0 == tokenLoan) {
      reserveIn = reserve1;
      reserveOut = reserve0;
    } else {
      reserveIn = reserve0;
      reserveOut = reserve1;
    }

    // calculate the targetAmountOut required for swap
    uint256 amountOut = uniV2Router.getAmountOut(
      amountFlashLoan,
      reserveIn,
      reserveOut
    );

    if (token0 == tokenLoan) {
      amount0Out = amountOut;
      // amount1Out = 0 (token 1 goes in)
    } else {
      amount1Out = amountOut;
      // amount0Out = 0 (token 0 goes in)
    }

    // trader has to return only amountIn
    targetAmount = amountFlashLoan;
  }

  function _setTrader(address newTrader) private {
    require(newTrader != trader, DUPLICATE);
    require(Address.isContract(newTrader), NOT_CONTRACT);

    trader = newTrader;
  }
}
","keccak256":"0x10ec485e08da99a93f0ba4ac574de2b835b4a4432f7e75dc97292b253017e423","license":"MIT"},"contracts/Trader.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity =0.8.18;

interface ITrader {
  event ExecutedTrade(
    address indexed tokenStart,
    address indexed tokenLast,
    uint256 amountIn,
    uint256 amountOut
  );
  function execute(bytes calldata routeData) external returns (uint256);
}

import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router01.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import { Route } from "./libraries/Route.sol";

contract UniswapV2Trader is ITrader, AccessControlEnumerable {
  using SafeERC20 for IERC20;

  string public constant NOT_ENOUGH_START_COLLATERAL = "Not enough collateral";
  string public constant ZERO_SLIPPAGE = "Zero slippage";
  string public constant SLIPPAGE_TOO_HIGH = "Slippage too high";
  string public constant DUPLICATE = "Duplicate";
  string public constant AMOUNT_OUT_TOO_LOW = "Amount out too low";

  bytes32 public constant EXECUTOR_ROLE = keccak256("EXECUTOR_ROLE");
  bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");
  uint256 public constant MIN_AMOUNT = 1_000; /// min swap amount

  constructor() {
    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
  }

  /// @dev No reentrancy possible, caller is the EXECUTOR_ROLE only (arbitrage contract)
  function execute(
    bytes calldata routeData
  ) external onlyRole(EXECUTOR_ROLE) returns (uint256 realAmountOut) {
    // walks through the route (token, router)[] and swaps assets
    // uses token[0] as start collateral, contract must be fulfilled before the trade
    (, uint256 amountOutMin, Route.SinglePath[] memory route) = Route
      .decodeRouteData(routeData);

    Route.validateRoute(route);

    (address tokenProfit, address tokenLoan) = Route.getFlashloanTokens(route);

    uint256 lastAmountOut;

    uint256 amountStartIn = IERC20(tokenLoan).balanceOf(address(this));

    require(amountStartIn >= MIN_AMOUNT, NOT_ENOUGH_START_COLLATERAL);

    // do not trade flashloan path (already done in flashloan contract)
    for (uint256 pathId = 1; pathId < route.length; ) {
      // swap through the current path
      // if pathId == 1, start with flashloan token (already received on contract)

      address router = route[pathId].router;
      uint256 amountIn = pathId == 1
        ? amountStartIn
        : IERC20(route[pathId].tokens[0]).balanceOf(address(this));

      lastAmountOut = _getAmountOut(amountIn, route[pathId].tokens, router);

      // swap through token path
      IERC20(route[pathId].tokens[0]).safeApprove(router, amountIn);
      IUniswapV2Router01(router).swapExactTokensForTokens(
        amountIn,
        0,
        route[pathId].tokens,
        address(this),
        block.timestamp + 1
      );

      unchecked {
        ++pathId;
      }
    }

    require(lastAmountOut >= amountOutMin, AMOUNT_OUT_TOO_LOW);

    realAmountOut = IERC20(tokenProfit).balanceOf(address(this));
    // fulfill trader caller
    IERC20(tokenProfit).safeTransfer(msg.sender, realAmountOut);

    emit ExecutedTrade(tokenLoan, tokenProfit, amountStartIn, realAmountOut);
  }

  function _getAmountOut(
    uint256 amountIn,
    address[] memory singlePath,
    address router
  ) private view returns (uint256) {
    uint256[] memory amounts = IUniswapV2Router01(router).getAmountsOut(
      amountIn,
      singlePath
    );

    // return the last token`s in chain amountOut
    return amounts[amounts.length - 1];
  }
}
","keccak256":"0x01e8f001ae8fa7ebf1ba9583345f9af86a031ab9b4abf23aa31cf04ae786308c","license":"MIT"},"contracts/libraries/Route.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

library Route {
  struct SinglePath {
    address router;
    address[] tokens;
  }

  string public constant INVALID_LEN = "Invalid len";
  string public constant ZERO_ADDRESS = "Zero address";
  string public constant INVALID_SINGLE_PATH = "Invalid path";
  string public constant INCONSISTENT_ROUTE = "Inconsistent route";
  string public constant NOT_CYCLE = "Not cycle";

  function encode(
    uint256 flashloanAmount,
    uint256 minAmountOut,
    SinglePath[] memory route
  ) internal pure returns (bytes memory res) {
    res = abi.encode(flashloanAmount, minAmountOut, route);
  }

  function decodeRouteData(
    bytes memory data
  ) internal pure returns (uint256 flashLoanAmount, uint256 minAmountOut, SinglePath[] memory route) {
    // ABI decode params
    (flashLoanAmount, minAmountOut, route) = abi.decode(data, (uint256, uint256, SinglePath[]));
  }

  function validateRoute(SinglePath[] memory route) internal pure {
    require(route.length != 0, INVALID_LEN);

    // do not check for max len and cycles in path (todo off-chain)
    // flashloan base
    _validateSinglePath(route[0], 0);

    for (uint256 i = 1; i < route.length; ++i) {
      _validateSinglePath(route[i], i);
      require(route[i].tokens[0] == _lastTokenInPath(route[i - 1]), INCONSISTENT_ROUTE);
    }

    // route must start and end with one token (this token is a profit maker)
    (address tokenFirst, address tokenLast) = getSideTokens(route);
    require(tokenFirst == tokenLast, NOT_CYCLE);
  }

  function _lastTokenInPath(SinglePath memory path) private pure returns(address) {
    uint256 len = path.tokens.length;
    return path.tokens[len - 1];
  }

  function _validateSinglePath(SinglePath memory path, uint256 pathId) private pure {
    if (pathId == 0)
     require(path.tokens.length == 2, INVALID_SINGLE_PATH);
    else 
    require(path.tokens.length >= 2, INVALID_SINGLE_PATH);

    require(path.router != address(0), ZERO_ADDRESS);

    for (uint256 j; j < path.tokens.length; ++j) {
      require(path.tokens[j] != address(0), ZERO_ADDRESS);
    }
  }

  function getSideTokens(
    SinglePath[] memory route
  ) internal pure returns (address tokenFlashloan, address tokenProfit) {
    uint256 routeLen = route.length;
    uint256 routeLastLen = route[routeLen - 1].tokens.length;

    return (route[0].tokens[0], route[routeLen - 1].tokens[routeLastLen - 1]);
  }

  function getFlashloanTokens(Route.SinglePath[] memory route) internal pure returns(address tokenProfit, address tokenLoan) {
    tokenProfit = route[0].tokens[0];
    tokenLoan = route[0].tokens[1];
  }
}
